def get_majority_element(arr):

    majority_element = -1

    for element in arr:

        count = 0
        for other in arr:

            if other == element:
                count = count + 1

        if count > 0 and majority_element == -1:
            majority_element = element

        if count > majority_element:
            majority_element = element

        # if element was majority and majority element found, break. 
        if majority_element != -1 and count >= majority_element:
            break

    return majority_element

assert get_majority_element([4, 7, 5, 3]) == 5
assert get_majority_element([5, 5, 5, 3]) == 5
assert get_majority_element([0, 3, 3, 0, 5, 2, 0, 2]) == 3
assert get_majority_element([]) == 0
assert get_majority_element([5]) == 5
assert get_majority_element([4]) == 4
